openAddEntityDialog();

function openAddEntityDialog() {
    customDialog.customDialog(htmlTemplate, AddEntityDialogController).subscribe();
}

function AddEntityDialogController(instance) {
    let vm = instance;

    vm.addEntityFormGroup = vm.fb.group({
        entityName: ['', [vm.validators.required]],
        attributes: vm.fb.group({
            temperatureAlarmFlag: [false],
            temperatureAlarmThreshold: [{value: null, disabled: true}],
            humidityAlarmFlag: [false],
            humidityAlarmThreshold: [{value: null, disabled: true}],
            latitude: [null],
            longitude: [null]
        })
    });

    vm.addEntityFormGroup.get('attributes').get('temperatureAlarmFlag').valueChanges
    .subscribe(activate => {
        if (activate) {
            vm.addEntityFormGroup.get('attributes').get('temperatureAlarmThreshold').enable();
        } else {
            vm.addEntityFormGroup.get('attributes').get('temperatureAlarmThreshold').disable();
        }
    });

    vm.addEntityFormGroup.get('attributes').get('humidityAlarmFlag').valueChanges
    .subscribe(activate => {
        if (activate) {
            vm.addEntityFormGroup.get('attributes').get('humidityAlarmThreshold').enable();
        } else {
            vm.addEntityFormGroup.get('attributes').get('humidityAlarmThreshold').disable();
        }
    });

    vm.save = function() {
        vm.addEntityFormGroup.markAsPristine();
        saveEntityObservable().subscribe(
            function (entity) {
                saveAttributes(entity.id).subscribe(
                    function () {
                        widgetContext.updateAliases();
                        vm.dialogRef.close(null);
                    }
                );
            }
        );
    };

    vm.cancel = function() {
        vm.dialogRef.close(null);
    };

    function saveEntityObservable() {
        const formValues = vm.addEntityFormGroup.value;
        let entity = {
            name: formValues.entityName,
            type: "Temperature Sensor"
        };
        return deviceService.saveDevice(entity);
    }

    function saveAttributes(entityId) {
        let attributes = vm.addEntityFormGroup.get('attributes').value;
        let attributesArray = [];
        for (let key in attributes) {
            if(attributes[key] !== null) {
                attributesArray.push({key: key, value: attributes[key]});
            }
        }
        if (attributesArray.length > 0) {
            return attributeService.saveEntityAttributes(entityId, "SERVER_SCOPE", attributesArray);
        } else {
            return widgetContext.rxjs.of([]);
        }
    }
}

